import * as d3 from "d3";
import { useRef, useEffect } from "react";
import Chart from "../charts/Chart";
import Circles from "../charts/Circles";

const Scatterplot = ({ data, xAccessor, yAccessor }) => {
  const ref = useRef();
  let dimensions = {
    width: width,
    height: width,

    margin: {
      top: 10,
      right: 60,
      bottom: 50,
      left: 50,
    },
  };

  const width = d3.min([window.innerWidth * 0.9, window.innerHeight * 0.9]);

  useEffect(() => {


    dimensions.boundedWidth =
      dimensions.width - dimensions.margin.left - dimensions.margin.right;
    dimensions.boundedHeight =
      dimensions.height - dimensions.margin.top - dimensions.margin.bottom;

    const wrapper = d3
      .select("#wrapper")
      .append("svg")
      .attr("width", dimensions.width)
      .attr("height", dimensions.height);

    const bounds = wrapper
      .append("g")
      .style(
        "transform",
        `translate(${dimensions.margin.left}px, ${dimensions.margin.top}px)`
      );

    const xScale = d3
      .scaleLinear()
      .domain(d3.extent(data, xAccessor))
      .range([0, dimensions.boundedWidth])
      .nice();

    const yScale = d3
      .scaleLinear()
      .domain(d3.extent(data, yAccessor))
      .range([dimensions.boundedHeight, 0])
      .nice();

    // const dots = bounds
    // bounds
    //   .selectAll("circle")
    //   .data(data)
    //   .enter()
    //   .append("circle")
    //   .attr("cx", (d) => xScale(xAccessor))
    //   .attr("cy", (d) => yScale(yAccessor))
    //   .attr("r", 5);
  }, [data]);
  // console.log(dots);

  return (
    <>
      <svg className="Scatterplot" dimensions={dimensions} ref={ref}>
        {/* <svg id="wrapper" ref={ref}>

        </svg> */}
        {data.map((d, i) => (
          <circle cx={xAccessor(d, i)} cy={yAccessor(d, i)} r="3" />
        ))}
        {/* <wrapper id="wrapper"></wrapper> */}
      </svg>
    </>
  );
};

export default Scatterplot;
