  return (
    <div className="border-4 border-amber-900	">
      <h2>Result Dashboard</h2>
      {loading && <div>Loading...</div>}
      {!loading && <ResultExampleGraph data={data} />}
    </div>
  );
}

export default ResultDashboard;
