import { useMemo } from "react";
import EventTable, { SelectColumnFilter } from "../components/EventTable";
import { useState, useEffect } from "react";

const url = "http://127.0.0.1:3000";

function EventsPage() {
  const [events, setEvents] = useState([]);
  const [years, setYears] = useState([]);

  useEffect(() => {
    fetch(url + '/years')
    .then(r => r.json())
    .then(years => setYears(years))
  }, [])

  useEffect(() => {
    // fetch(url + `/events/years/${year}`)
    fetch(url + `/events/years/2021`)
      .then((r) => r.json())
      .then((events) => {
        // setEvents(events);
        // FIXME: temporary solution
        setEvents(events.filter((value) => Object.keys(value).length !== 0));
      })
      .catch((error) => {
        console.log("error");
      });
  }, []);

  const columns = useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "Location",
        accessor: "location",
        Filter: SelectColumnFilter, // new
        filter: "includes", // new
      },
      {
        Header: "Date",
        accessor: "date",
      },
      // TODO
      {
        Header: "",
        accessor: "year",
        Filter: SelectColumnFilter, // new
        filter: "includes", // new
      },
    ],
    []
  );

  function handleChange(e) {

  }

  return (
    <div className="flex justify-center">
      <select>

      </select>



        <select name="type" id="type" aria-label="type" onChange={handleChange}>
          <option value="all">All</option>
          <option value="cat">Cats</option>
          <option value="dog">Dogs</option>
          <option value="micropig">Micropigs</option>
        </select>







      {!events.length ? (
        <div>Loading..</div>
      ) : (
        <EventTable columns={columns} events={events} />
      )}
    </div>
  );
}

export default EventsPage;
